import { useState } from "react";
import SuccessModal from "./SuccessModal";

const ReferralForm = ({ onClose }) => {
  const [referrerName, setReferrerName] = useState("");
  const [referrerEmail, setReferrerEmail] = useState("");
  const [refereeName, setRefereeName] = useState("");
  const [refereeEmail, setRefereeEmail] = useState("");
  const [course, setCourse] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const response = await fetch("http://localhost:5000/api/referrals/submit", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ referrerName, referrerEmail, refereeName, refereeEmail, course }),
    });

    if (response.ok) {
      setSuccessMessage("Referral submitted successfully!");
      setTimeout(() => {
        setSuccessMessage("");
        onClose(); 
      }, 1500);
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
      {successMessage ? (
        <SuccessModal message={successMessage} onClose={onClose} />
      ) : (
        <div className="bg-white shadow-md p-6 rounded w-96">
          <h2 className="text-xl font-bold mb-4">Refer a Friend</h2>
          <form onSubmit={handleSubmit}>
            <input type="text" placeholder="Your Name" className="w-full p-2 mb-2 border" onChange={(e) => setReferrerName(e.target.value)} required />
            <input type="email" placeholder="Your Email" className="w-full p-2 mb-2 border" onChange={(e) => setReferrerEmail(e.target.value)} required />
            <input type="text" placeholder="Friend's Name" className="w-full p-2 mb-2 border" onChange={(e) => setRefereeName(e.target.value)} required />
            <input type="email" placeholder="Friend's Email" className="w-full p-2 mb-2 border" onChange={(e) => setRefereeEmail(e.target.value)} required />
            <input type="text" placeholder="Course Name" className="w-full p-2 mb-4 border" onChange={(e) => setCourse(e.target.value)} required />
            <div className="flex justify-between">
              <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Submit</button>
              <button type="button" onClick={onClose} className="bg-red-500 text-white px-4 py-2 rounded">Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default ReferralForm;
