import { useState } from "react";

const ReferralForm = ({ onClose, onSuccess }) => {
  const [referrerName, setReferrerName] = useState("");
  const [referrerEmail, setReferrerEmail] = useState("");
  const [refereeName, setRefereeName] = useState("");
  const [refereeEmail, setRefereeEmail] = useState("");
  const [course, setCourse] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:5000/api/referral/submit", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ referrerName, referrerEmail, refereeName, refereeEmail, course }),
    });

    const data = await response.json();
    console.log(data);

    if (response.ok) {
      onSuccess(); // Close form and show success modal
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white rounded-lg shadow-xl flex w-[90%] md:w-[800px]">
        {/* Left Side - Image */}
        <div className="hidden md:flex w-1/2 bg-blue-500 p-6 items-center justify-center bg">
          <img src="/assets/image.png" alt="Refer & Earn" className="max-w-full" />
        </div>

        {/* Right Side - Form */}
        <div className="w-full md:w-1/2 p-6 relative">
          <button className="absolute top-4 right-4 text-gray-500 hover:text-gray-800" onClick={onClose}>
            âœ•
          </button>

          <h2 className="text-xl font-bold text-gray-900">
            Refer your <span className="text-blue-600">friend!</span>
          </h2>

          <div className="mt-3 flex items-center space-x-2">
            <div className="w-3 h-3 bg-blue-600 rounded-full"></div>
            <div className="w-14 h-1 bg-gray-300"></div>
            <div className="w-3 h-3 bg-gray-300 rounded-full"></div>
          </div>

          <p className="text-gray-500 mt-2">Friend's details</p>

          <form onSubmit={handleSubmit} className="mt-4 space-y-3">
            <input type="text" placeholder="Enter friend's name" className="w-full px-4 py-2 border rounded-lg" onChange={(e) => setReferrerName(e.target.value)} required />
            <input type="email" placeholder="Enter friend's email" className="w-full px-4 py-2 border rounded-lg" onChange={(e) => setReferrerEmail(e.target.value)} required/>
            <input type="text" placeholder="Enter your name" className="w-full px-4 py-2 border rounded-lg" onChange={(e) => setRefereeName(e.target.value)} required />
            <input type="email" placeholder="Enter your email" className="w-full px-4 py-2 border rounded-lg" onChange={(e) => setRefereeEmail(e.target.value)} required/>
            <select className="w-full px-4 py-2 border rounded-lg" onChange={(e) => setCourse(e.target.value)} required >
              <option>Select Vertical</option>
              <option>Technology</option>
              <option>Marketing</option>
              <option>Finance</option>
            </select>
            <button type="submit" className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ReferralForm;
