import { Link } from "react-router-dom";
import React from "react";
import image from "../assets/image.png";

const Home = () => {
  return (
    <div className="w-full min-h-screen bg-gray-100 flex flex-col items-center py-20 px-8 md:px-0">
      <div className="w-full max-w-6xl bg-white rounded-2xl shadow-2xl p-16 md:p-20 mt-12 flex flex-col md:flex-row items-center gap-16 relative">
        <div className="md:w-1/2 text-center md:text-left">
          <h2 className="text-5xl font-extrabold text-gray-900 leading-snug">Let's Learn & Earn</h2>
          <p className="text-2xl text-gray-700 mt-6">Get a chance to win up to <span className="text-blue-600 font-bold">Rs. 15,000</span></p>
          <Link to="/refer" className="mt-8 inline-block bg-blue-600 text-white text-xl px-10 py-4 rounded-xl shadow-lg hover:bg-blue-700 transition-all">Refer Now</Link>
        </div>

        <div className="md:w-1/2 flex justify-center relative">
          <img
            src={image}
            alt="Mockup"
            className="w-64 md:w-80 shadow-xl rounded-xl"
          />
          <div className="absolute -top-10 right-10 w-16 h-16 bg-green-300 rounded-full"></div>
          <div className="absolute bottom-10 left-10 w-16 h-16 bg-green-300 rounded-full"></div>
        </div>
      </div>
    </div>
  );
};

export default Home;
