import { useState } from "react";
import ReferralForm from "../components/ReferralForm";
import SuccessModal from "../components/SuccessModal";

const ReferralPage = () => {
  const [showForm, setShowForm] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);

  return (
    <div className="flex flex-col items-center mt-10">
        
      <h2 className="text-2xl font-bold mb-4">Refer a Friend</h2>
      {!showForm && (
        <button onClick={() => setShowForm(true)} className="bg-blue-500 text-white px-4 py-2 rounded">
          Refer Now
        </button>
      )}
      {showForm && <ReferralForm onClose={() => setShowForm(false)} onSuccess={() => { setShowForm(false); setShowSuccessModal(true); }} />}
      {showSuccessModal && <SuccessModal message="Referral submitted successfully!" onClose={() => setShowSuccessModal(false)} />}
    </div>
  );
};

export default ReferralPage;
