import { Link } from "react-router-dom";
import React from "react";
import image from "../assets/image.png";

const Home = () => {
  return (
    <div className="w-full min-h-[70vh] bg-gray-100 flex flex-col items-center py-2 px-2 md:px-0">
      <div className="w-full max-w-5xl bg-white rounded-xl shadow-xl p-10 md:p-14 mt-10 flex flex-col md:flex-row items-center gap-10 relative min-h-[500px]">
        <div className="md:w-1/3 text-center md:text-left">
          <h2 className="text-4xl font-bold text-gray-900 leading-snug">Let's Learn & Earn</h2>
          <p className="text-lg text-gray-700 mt-4">Get a chance to win up to <span className="text-blue-600 font-semibold">Rs. 15,000</span></p>
          <Link to="/refer" className="mt-6 inline-block bg-blue-600 text-white px-8 py-3 rounded-lg shadow-md hover:bg-blue-700 transition-all">Refer Now</Link>
        </div>

        <div className="md:w-2/3 flex justify-center relative">
          <img
            src={image}
            alt="Mockup"
            className="w-48 md:w-60 shadow-lg rounded-lg"
          />
          <div className="absolute -top-8 right-2 w-12 h-12 bg-green-300 rounded-full"></div>
          <div className="absolute bottom-8 left-8 w-12 h-12 bg-green-300 rounded-full"></div>
        </div>
      </div>
    </div>
  );
};

export default Home;
