import { Link } from "react-router-dom";
import React from "react";
import image from "../assets/image.png";

const Home = () => {
  return (
    <div className="w-full min-h-screen bg-gray-100 flex flex-col items-center py-10">
      <div className="w-full max-w-4xl bg-white rounded-xl shadow-lg p-8 mt-10 flex flex-col md:flex-row items-center relative">
        <div className="md:w-1/2">
          <h2 className="text-3xl font-bold text-gray-900">Let's Learn & Earn</h2>
          <p className="text-lg text-gray-700 mt-2">Get a chance to win up to <span className="text-blue-600 font-semibold">Rs. 15,000</span></p>
          <Link to="/refer" className="mt-4 bg-blue-600 text-white px-6 py-3 rounded-lg">Refer Now</Link>
        </div>

        <div className="md:w-1/2 flex justify-center mt-6 md:mt-0 relative">
          <img
            src={image}
            alt="Mockup"
            className="w-40 shadow-lg rounded-lg"
          />
          <div className="absolute -top-6 right-6 w-10 h-10 bg-green-300 rounded-full"></div>
          <div className="absolute bottom-6 left-6 w-10 h-10 bg-green-300 rounded-full"></div>
        </div>
      </div>
    </div>
  );
};

export default Home;
